sys_script_fix.script:
roleNames = 'sn_gsm.constituent_viewer,sn_gsm.report_viewer,sn_gsm.case_read_granular,sn_gsm_lic_prmt.case_writer,sn_gsm.constituent_contributor,sn_gsm.case_write_granular,sn_gsm_lic_prmt.ib_viewer,sn_gsm_lic_prmt.case_viewer,sn_gsm.business_agent,sn_gsm_lic_prmt.contributor_creator,sn_gsm.constituent_writer,sn_gsm.contributor_creator,sn_gsm.relationship_agent,sn_gsm.constituent_agent,sn_gsm.service_offered_viewer,sn_gsm.case_writer,sn_gsm.service_offered_writer,sn_gsm_lic_prmt.business_agent,sn_gsm.case_create_granular,sn_gsm_lic_prmt.constituent_agent,gsm_lic_prmt.relationship_agent,sn_gsm.case_viewer,sn_gsm_lic_prmt.report_viewer';

var roles = new GlideRecord('sys_user_role');
var roleSysIDs = new Array();

roles.addQuery('name','IN', roleNames);
roles.query();

while(roles.next()){
 roleSysIDs.push(roles.sys_id);   
}

 gs.info(roleSysIDs);
